import json
from string import punctuation
from urlparse import urlparse
from django.core.files import File


def cleanTweet(tweet_text):
    '''
    clean the tweeets from the incoming stream of data
    :param tweet_text:
    :return:
    '''
    pt = tweet_text.lower()
    processedTweet = ''

    for i in pt.split():
        s, n, p, pa, q, f = urlparse(i)
        if s and n:
            pass
        elif i[:1] == '@':
            pass
        elif i[:1] == '#':
            processedTweet = processedTweet.strip() + ' ' + i[1:]
        else:
            processedTweet = processedTweet.strip() + ' ' + i

    for i in list(punctuation):
        processedTweet = processedTweet.replace(i, '')

    return processedTweet


