def sentimentAnalysis(data):
    tweet = json.loads(data)
    if 'text' in tweet:
        tweet_text = tweet['text']
        tweet_processed = cleanTweet(tweet_text)
        sentiments = sentiment_list()
        tweets = tweet_list(tweet_processed)
        # print tweets
        sent_score = 0
        for index in range(len(tweets)):
            tweet_word = tweets[index].split()
            for word in tweet_word:
                if not (word.encode('utf-8', 'ignore') == ""):
                    if word in sentiments.keys():
                        sent_score = sent_score + float(sentiments[word])
                    else:
                        sent_score = sent_score
        print(sent_score)
        return sent_score, tweet_text
